def parseHTML(html)
	# components will have an array of objects that each
	# preferably hold at least the following attributes:
	# 1. component type (i.e image, video, URL link)
	# 2. content (i.e image link, video link, URL)
	# 3. if possible, can you try to include a hash
	# that contains all other HTML tags in the element?
	# something like {"class" => "body", "media" => "handheld"}
#	components = [ [[image,name,class,src],[image,name,class,src]], [[iframe,class,src],[iframe,class,src]], [[href,class,link],[href,link,class]] ]
#   first is images, second is iframes, third is hrefs

	# parse html here, nokogiri is probably a reasonable
	# option to try first
	
#puts source
	images = []
	iframes = []
	hrefs = []
	#tmp = '<img class="rg_i" name="PbOujaQ69A7CCM:" data-sz="f" onload="google.stb.csi.onTbn(1, this)" style="width: 280px; height: 180px; margin-left: 0px; margin-right: 0px; margin-top: 0px;" src="data:image/jpeg;base64,">'
	imageTags = html.scan(/<img.*? class=[\"'](.+?)[\"'].*?name=[\"'](.+?)[\"'].*?src=[\"'](.+?)[\"']/)
	iframeTags = html.scan(/<iframe.*?class=[\"'](.+?)[\"'].*?src=[\"'](.+?)[\"']/)
	hrefTags = html.scan(/<a href=[\"'](.+?)[\"'].*class=[\"'](.+?)[\"']/)
	imageTags.each{|x| 
			tmp = [] 
			tmp.push("image")
			x.each{|y|
				tmp.push(y)  
			}
			images.push(tmp)
		}
	iframeTags.each{|x| 
			tmp = [] 
			tmp.push("iframe")
			x.each{|y|
				tmp.push(y)  
			}
			iframes.push(tmp)
		}
	hrefTags.each{|x| 
		tmp = [] 
		tmp.push("href")
		x.each{|y|
			tmp.push(y)  
		}
		hrefs.push(tmp)
	}
	components = []
	components.push(images)
	components.push(iframes)
	components.push(hrefs)
return components
